'   gen_no_change EXCEPTION;'||chr(10)||
'   PRAGMA EXCEPTION_INIT(gen_no_change, -20008);'||chr(10)||
'   MRI_COUNT  number := 0;'||chr(10)||
'   MRU_COUNT  number := 0;'||chr(10)||
'   rows       number;'||chr(10)||
