declare
  p varchar2(32767) := null;
  l_clob clob;
  l_length number := 1;
begin
p:=p||'declare'||chr(10)||
'   gen_no_change EXCEPTION;'||chr(10)||
'   PRAGMA EXCEPTION_INIT(gen_no_change, -20008);'||chr(10)||
'   MRI_COUNT  number := 0;'||chr(10)||
'   MRU_COUNT  number := 0;'||chr(10)||
'   rows       number;'||chr(10)||
'begin'||chr(10)||
'   -- util.ignore_no_change := FALSE;'||chr(10)||
'   for i in 1 .. apex_application.g_f02.count'||chr(10)||
'   loop'||chr(10)||
'      rows := 0;'||chr(10)||
'      if apex_application.g_f02(i) is not null'||chr(10)||
'      then'||chr(10)||
'         begin'||chr(10)||
'            update #OWNER#.check_cons_act'||chr(10)||
'         ';

p:=p||'     set  tables_nk1 = '||chr(10)||
'                      "#OWNER#".applications_dml.get_nk(:P1_APP_ID)'||chr(10)||
'                  ,tables_nk2 = '||chr(10)||
'                      :P41_TABLES_NK2'||chr(10)||
'                  ,seq = '||chr(10)||
'                      apex_application.g_f03(i)'||chr(10)||
'                  ,text = '||chr(10)||
'                      apex_application.g_f04(i)'||chr(10)||
'                  ,description = '||chr(10)||
'                      apex_application.g_f05(i)'||chr(10)||
'      ';

p:=p||'       where id = apex_application.g_f02(i);'||chr(10)||
'            rows := SQL%ROWCOUNT;'||chr(10)||
'         exception'||chr(10)||
'            when gen_no_change'||chr(10)||
'            then'||chr(10)||
'               -- A matching record was found, but the data'||chr(10)||
'               -- in the record is the same as the new data'||chr(10)||
'               goto NEXT_ITERATION;'||chr(10)||
'            when others'||chr(10)||
'            then'||chr(10)||
'               raise;'||chr(10)||
'         end;'||chr(10)||
'      end if;'||chr(10)||
'      i';

p:=p||'f rows = 0'||chr(10)||
'      then'||chr(10)||
'         insert into #OWNER#.check_cons_act'||chr(10)||
'               (tables_nk1'||chr(10)||
'               ,tables_nk2'||chr(10)||
'               ,seq'||chr(10)||
'               ,text'||chr(10)||
'               ,description'||chr(10)||
'               )'||chr(10)||
'         values '||chr(10)||
'               ("#OWNER#".applications_dml.get_nk(:P1_APP_ID)'||chr(10)||
'               ,:P41_TABLES_NK2'||chr(10)||
'               ,apex_application.g_f03(i)'||chr(10)||
