'   -- util.ignore_no_change := FALSE;'||chr(10)||
'   for i in 1 .. apex_application.g_f02.count'||chr(10)||
'   loop'||chr(10)||
'      rows := 0;'||chr(10)||
'      if apex_application.g_f02(i) is not null'||chr(10)||
'      then'||chr(10)||
'         begin'||chr(10)||
'            update #OWNER#.programs_act'||chr(10)||
