'                  ,type = '||chr(10)||
'                      apex_application.g_f04(i)'||chr(10)||
'                  ,description = '||chr(10)||
'                      apex_application.g_f05(i)'||chr(10)||
'             where id = apex_application.g_f02(i);'||chr(10)||
'            rows := SQL%ROWCOUNT;'||chr(10)||
'         exception'||chr(10)||
'            when ';

p:=p||'gen_no_change'||chr(10)||
'            then'||chr(10)||
