'   then'||chr(10)||
'      :P81_MRI_COU';

p:=p||'NT := MRI_COUNT;'||chr(10)||
'      :P81_MRU_COUNT := MRU_COUNT;'||chr(10)||
'   end if;'||chr(10)||
